import React, { useState } from 'react';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import logo from './smartfix.png';
import { Link, useHistory } from 'react-router-dom';

function Login() {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const history = useHistory();

  const handleLogin = () => {
    if (!username || !password) {
      toast.error('Please fill in all the required fields.', { position: toast.POSITION.TOP_CENTER });
      return;
    }

    // Assuming you have a separate API endpoint for login
    fetch('https://api.example.com/login', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ username, password }),
    })
      .then((response) => response.json())
      .then((data) => {
        if (data.success) {
          toast.success('Login successful!', { position: toast.POSITION.TOP_CENTER });
          history.push('/home');
        } else {
          toast.error('Invalid username or password', { position: toast.POSITION.TOP_CENTER });
        }
      })
      .catch((error) => {
        toast.error('Error occurred while logging in!', { position: toast.POSITION.TOP_CENTER });
      });
  };

  return (
    <div>
      <div>
        <div>
          <img src={logo} className='image' alt='Logo' />
        </div>
      </div>

      <body>
        <div className='container'>
          <h1>User Login</h1>
          <div>
            <input type='text' name='username' placeholder='Username' onChange={(e) => setUsername(e.target.value.trim())} />
            <input type='password' name='password' placeholder='Password' onChange={(e) => setPassword(e.target.value)} />
            <button className='button-3' onClick={handleLogin}>
              Login
            </button>
          </div>
          <div>
            <Link className='text' to='/signup'>
              <h5>Or sign Up Using</h5>
            </Link>
          </div>
        </div>
      </body>
      <ToastContainer />
    </div>
  );
}

export default Login;
