import React, { useState } from 'react';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import logo from './smartfix.png';
import 'react-toastify/dist/ReactToastify.css';
import { Link } from 'react-router-dom';

function Signup() {
  const [username, setUsername] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');

  const handleSignup = () => {
    if (!username || !email || !password) {
      toast.error('Please fill in all the required fields.', { position: toast.POSITION.TOP_CENTER });
      return;
    }

    const userData = {
      username: username,
      email: email,
      password: password,
    };

    // Rest of the code to handle signup

  };

  return (
    <div>
      <header>
        <div>
          <img src={logo} alt='logo' className='image' />
        </div>
      </header>
      <body>
        <div className='container'>
          <h1>User Registration</h1>
          <div>
            <input
              type='text'
              name='username'
              placeholder='Username'
              onChange={(e) => setUsername(e.target.value.trim())}
            />
            <input
              type='email'
              name='email'
              placeholder='Email'
              onChange={(e) => setEmail(e.target.value.trim())}
            />
            <input
              type='password'
              name='password'
              placeholder='Password'
              onChange={(e) => setPassword(e.target.value)}
            />
            <button className='button-29' onClick={handleSignup}>
              Submit
            </button>
            <Link className='text' to='/login'>
              <h5>Back to Login</h5>
            </Link>
          </div>
        </div>
      </body>
      <ToastContainer />
    </div>
  );
}

export default Signup;
