import { useEffect, useState } from 'react';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import logo from './smartfix.png';
import 'react-toastify/dist/ReactToastify.css';


function Login() {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');

  const handleLogin = () => {
    const loginData = {
      username: username,
      password: password,
    };

    const requestOptions = {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(loginData),
    };

    fetch('https://646b0c027d3c1cae4ce31370.mockapi.io/new', requestOptions)
      .then((response) => response.json())
      .then((data) => {
        if (data.length > 0) {
          toast.success('Login successful!', { position: toast.POSITION.TOP_CENTER });
        } else {
          toast.error('Invalid username or password!', { position: toast.POSITION.TOP_CENTER });
        }
      })
      .catch((error) => {
        toast.error('Error occurred while logging in!', { position: toast.POSITION.TOP_CENTER });
      });
  };

  return (
    <div>
      <div>
        <div>
          <img src={logo} className='image' alt='Logo' />
        </div>
      </div>
      <body>
        <div className='container'>
          <h1>User Login</h1>
          <div>
            <input type='text' name='username' placeholder='Username' onChange={(e) => setUsername(e.target.value)} required />
            <input type='password' name='password' placeholder='Password' onChange={(e) => setPassword(e.target.value)} required />
            <button className='button-29' onClick={handleLogin}>Login</button>
          </div>
        </div>
      </body>
      <ToastContainer />
    </div>
  );
}

export {  Login };
